package com.example.SocialNetwork.api;

import com.example.SocialNetwork.configuration.Constants;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.web.bind.annotation.*;

import java.util.ArrayList;
import java.util.List;
import java.util.concurrent.atomic.AtomicInteger;

@RestController
@RequestMapping(Constants.API_URL + PostController.URL)
public class PostController {
    public static final String URL = "/posts";
    private final Logger log = LoggerFactory.getLogger(UserController.class);
    private final AtomicInteger idGenerator = new AtomicInteger();
    private final List<PostDTO> posts;
    private UserController userController;

    public PostController(UserController userController) {
        this.userController = userController;
        this.posts = new ArrayList<>(List.of(
                new PostDTO(idGenerator.incrementAndGet(), 1, "https://encrypted-tbn1.gstatic.com/images?q=tbn:ANd9GcSvuK_sYP3ndNvGc_CIaHSbXaNjfKV9-B9QljORmibwRZLHxJISKIc2sfY1m7gEYBQTnmY3BQsebKHXFYrOgxw-fP2Tj0uE2FtU1tiPXI633Q",
                        "nvefj eorhgef bep eirgheg fvh"),
                new PostDTO(idGenerator.incrementAndGet(), 2, "data:image/jpeg;base64,",
                        "aieughoeitgnviruvnhsoufnhvr"),
                new PostDTO(idGenerator.incrementAndGet(), 1, "data:image/jpeg;base64,",
                        "kdjvwv")));
        this.posts.forEach(post -> post.setUserName(userController.get(post.getUserId()).getUserName()));
        this.posts.forEach(post -> post.setUserAvatarURL(userController.get(post.getUserId()).getUserAvatarURL()));
    }

    @GetMapping
    public List<PostDTO> getAll() {
        return posts;
    }

    @GetMapping("/{id}")
    public PostDTO get(@PathVariable int id) {
        return posts.stream()
                .filter(group -> group.getId() == id)
                .findAny()
                .orElseThrow(() -> new NotFoundException(PostDTO.class, id));
    }

    @PostMapping
    public PostDTO create(@RequestBody PostDTO newPost){
        newPost.setId(idGenerator.incrementAndGet());
        posts.add(newPost);
        this.posts.forEach(post -> post.setUserName(userController.get(post.getUserId()).getUserName()));
        this.posts.forEach(post -> post.setUserAvatarURL(userController.get(post.getUserId()).getUserAvatarURL()));

        return posts.getLast();
    }

    @PutMapping("/{id}")
    public PostDTO update(@PathVariable int id, @RequestBody PostDTO newPost) {
        log.debug("Edit student with id {} and data {}", id, newPost);
        final PostDTO existsPost = get(id);
        existsPost.setUserId(newPost.getUserId());
        existsPost.setUserAvatarURL(newPost.getUserAvatarURL());
        existsPost.setPostImageURL(newPost.getPostImageURL());
        existsPost.setPostTextContent(newPost.getPostTextContent());
        return existsPost;
    }

    @DeleteMapping("/{id}")
    public PostDTO delete(@PathVariable int id) {
        log.debug("Delete student with id {}", id);
        final PostDTO post = get(id);
        posts.remove(post);
        return post;
    }
}
