package com.example.SocialNetwork.api;

import com.example.SocialNetwork.configuration.Constants;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.web.bind.annotation.*;

import java.util.ArrayList;
import java.util.List;
import java.util.concurrent.CopyOnWriteArrayList;
import java.util.concurrent.atomic.AtomicInteger;

@RestController
@RequestMapping(Constants.API_URL + UserController.URL)
public class UserController {
    public static final String URL = "/user";
    private final Logger log = LoggerFactory.getLogger(UserController.class);
    private final AtomicInteger idGenerator = new AtomicInteger();
    private List<UserDTO> users = new CopyOnWriteArrayList<>();

    public UserController() {
        this.users = new ArrayList<>(List.of(
                new UserDTO(idGenerator.incrementAndGet(), "marina", "aleksandrova", "ma", "data:image/jpeg;base64,",
                        "ffff", "https://MA/profile", 2, 11, 45,
                        "+7 (999) 999-99-99", new ArrayList<>(List.of())),
                new UserDTO(idGenerator.incrementAndGet(), "maksim", "ivleev", "iv", "data:image/jpeg;base64,",
                        "jkkl", "https://MI/profile", 45, 246, 45,
                        "+7 (999) 888-88-88", new ArrayList<>(List.of())),
                new UserDTO(idGenerator.incrementAndGet(), "lera", "milkova", "lm", "data:image/jpeg;base64,",
                        "jyfd", "https://LM/profile", 6, 46, 23,
                        "+7 (999) 777-77-77", new ArrayList<>(List.of()))));

    }

    @GetMapping
    public List<UserDTO> getMethodName() {
        log.debug("Get all users");
        return users.stream().toList();
    }

    @GetMapping("/{id}")
    public UserDTO get(@PathVariable int id) {
        log.debug("Get user wtih id {}", id);
        return users.stream()
                .filter(user -> user.getId() == id)
                .findAny()
                .orElseThrow(() -> new NotFoundException(UserDTO.class, id));
    }

    @PostMapping
    public UserDTO create(@RequestBody UserDTO newUser) {
        log.debug("Create user with data {}", newUser);
        newUser.setId(idGenerator.incrementAndGet());
        users.add(newUser);
        return newUser;
    }

    @PutMapping("/{id}")
    public UserDTO edit(@PathVariable int id, @RequestBody UserDTO newUser) {
        log.debug("Edit student with id {} and data {}", id, newUser);
        final UserDTO existsUser = get(id);
        existsUser.setFirstName(newUser.getFirstName());
        existsUser.setLastName(newUser.getLastName());
        existsUser.setUserName(newUser.getUserName());
        existsUser.setUserAvatarURL(newUser.getUserAvatarURL());
        existsUser.setPublications(newUser.getPublications());
        existsUser.setFollowers(newUser.getFollowers());
        existsUser.setSubscriptions(newUser.getSubscriptions());
        existsUser.setPageAddress(newUser.getPageAddress());
        existsUser.setPhone(newUser.getPhone());
        return existsUser;
    }

    @DeleteMapping("/{id}")
    public UserDTO delete(@PathVariable int id) {
        log.debug("Delete student with id {}", id);
        final UserDTO user = get(id);
        users.remove(user);
        return user;
    }
}
